import { useState } from "react"

export default function CrudApiTask () {

    const [wilayah, setWilayah] = useState([])


    function getListWilayah () {
        axios.get('http://localhost:3001/wilayah').then((res) => {
            setWilayah(res.data)
        })
    }
    
    return<>

    <h2> Form Wilayah </h2>
    <label>
        Provinsi :
        <input type="text"
        value={formInput.provinsi} 
        onChange={event => handleInput(event, 'provinsi')}
        />
    </label>
    <br/>
    <br/>
    <label>
        Kota :
        <input type="text"
        value={formInput.provinsi} 
        onChange={event => handleInput(event, 'provinsi')}
        />
    </label>
    <button onClick={() => getListWilayah()}>
        submit
    </button>
    
    <br/><hr/><br/>
    
    <h2>
        Daftar Wilayah :
    </h2>
    <ul>
        {wilayah.map (wilayah => 
        <li key={wilayah.id}>
            Provinsi : {wilayah.provinsi} | Kota : {wilayah.kota} | Kecamatan : {wilayah.kecamatan}
        </li>
        )}
    </ul>
    </>
}
